export const SideBar = ({ collapsed }: { collapsed: boolean }) => {
  const { RoleID } = useSelector((state: RootState) => state.user);
  const [routeList, setRouteList] = useState<ItemType<MenuItemType>[]>([]);

  const pathname = usePathname();
  const router = useRouter();

  useEffect(() => {
    if (RoleID === 1) {
      setRouteList(adminRoutes);
    } 
    
    if (RoleID === 2) {
      setRouteList(editorRoutes);
    }

    if (RoleID === 3) {
      setRouteList(contentRoutes)
    }
  }, [RoleID]);

  return (
    <Sider
      trigger={null}
      collapsible
      collapsed={collapsed}
      className="manager-sider"
    >
      <div className="sider-logo">
        <Image
          alt="eco-cupid logo"
          src="/svg/logo.png"
          width={60}
          height={60}
          className="sider-logo-image"
          preview={false}
        />
      </div>
      <Menu
        mode="inline"
        className="manager-menu"
        defaultSelectedKeys={[pathname]}
        items={routeList}
        onClick={(item) => {
          router.push(item.key);
        }}
      />
    </Sider>
  );
};
